import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import Markdown from 'markdown-to-jsx';
import styled from 'styled-components';

const PostContainer = styled.div`
  max-width: 800px;
  margin: 0 auto;
  padding: 2rem;
`;

const PostTitle = styled.h1`
  color: #333;
  margin-bottom: 1rem;
`;

const PostContent = styled.div`
  line-height: 1.8;
  color: #444;

  h2, h3 {
    margin-top: 2rem;
  }

  p {
    margin-bottom: 1.5rem;
  }
`;

const BlogPost = () => {
  const { slug } = useParams();
  const [post, setPost] = useState(null);

  useEffect(() => {
    // In a real application, you would fetch the post content from your backend
    // This is just a mock example
    const fetchPost = async () => {
      try {
        // Simulate API call
        const mockPost = {
          title: "Understanding Home Healthcare",
          content: `
# Understanding Home Healthcare

Home healthcare is becoming increasingly important in modern healthcare delivery...

## Benefits of Home Healthcare

1. Comfort of familiar surroundings
2. Personalized care
3. Cost-effective

## How It Works

Home healthcare providers work closely with patients...
          `
        };
        setPost(mockPost);
      } catch (error) {
        console.error('Error fetching post:', error);
      }
    };

    fetchPost();
  }, [slug]);

  if (!post) return <div>Loading...</div>;

  return (
    <PostContainer>
      <PostTitle>{post.title}</PostTitle>
      <PostContent>
        <Markdown>{post.content}</Markdown>
      </PostContent>
    </PostContainer>
  );
};

export default BlogPost;
