import React from 'react';
import styled from 'styled-components';
import { motion, AnimatePresence } from 'framer-motion';
import { Link } from 'react-scroll';

const MenuOverlay = styled(motion.div)`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100vh;
  background: rgba(0, 0, 0, 0.7);
  z-index: 1000;
  display: none;

  @media (max-width: 768px) {
    display: block;
  }
`;

const MenuContainer = styled(motion.div)`
  position: fixed;
  top: 0;
  right: 0;
  width: 300px;
  height: 100vh;
  background: linear-gradient(135deg, #2C2320, #3D2B2B);
  padding: 4rem 2rem 2rem;
  z-index: 1001;
  display: flex;
  flex-direction: column;
  gap: 1rem;
  box-shadow: -5px 0 20px rgba(0, 0, 0, 0.2);
`;

const CloseButton = styled.button`
  position: absolute;
  top: 1rem;
  right: 1rem;
  background: none;
  border: none;
  font-size: 1.5rem;
  cursor: pointer;
  color: white;
`;

const MobileNavLink = styled(Link)`
  font-size: 1.2rem;
  padding: 1rem;
  cursor: pointer;
  transition: color 0.3s ease;
  color: white;

  &:hover {
    color: var(--color-accent);
  }
`;

const MobileMenu = ({ isOpen, onClose }) => {
  return (
    <AnimatePresence>
      {isOpen && (
        <>
          <MenuOverlay
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            exit={{ opacity: 0 }}
            onClick={onClose}
          />
          <MenuContainer
            initial={{ x: 300 }}
            animate={{ x: 0 }}
            exit={{ x: 300 }}
            transition={{ type: "spring", damping: 20 }}
          >
            <CloseButton onClick={onClose}>Ã—</CloseButton>
            <MobileNavLink to="about" smooth={true} duration={500} onClick={onClose}>
              About
            </MobileNavLink>
            <MobileNavLink to="features" smooth={true} duration={500} onClick={onClose}>
              Features
            </MobileNavLink>
            <MobileNavLink to="testimonials" smooth={true} duration={500} onClick={onClose}>
              Testimonials
            </MobileNavLink>
            <MobileNavLink to="contact" smooth={true} duration={500} onClick={onClose}>
              Contact
            </MobileNavLink>
          </MenuContainer>
        </>
      )}
    </AnimatePresence>
  );
};

export default MobileMenu;
