import React, { useState, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';
import styled from 'styled-components';

const PostContainer = styled.div`
  max-width: 800px;
  margin: 120px auto 60px;
  padding: 2rem;
`;

const BackButton = styled(Link)`
  display: inline-block;
  margin-bottom: 2rem;
  color: #01bf71;
  text-decoration: none;

  &:hover {
    text-decoration: underline;
  }
`;

const PostTitle = styled.h1`
  color: #333;
  margin-bottom: 1rem;
`;

const PostContent = styled.div`
  line-height: 1.8;
  color: #444;

  h2, h3 {
    margin-top: 2rem;
  }

  p {
    margin-bottom: 1.5rem;
  }
`;

const PostDate = styled.div`
  color: #666;
  margin-bottom: 2rem;
`;

const BlogPost = () => {
  const { slug } = useParams();
  const [post, setPost] = useState(null);

  useEffect(() => {
    // In a real app, you would fetch the post data from an API
    // This is mock data for demonstration
    const mockPosts = {
      'understanding-home-healthcare': {
        title: 'Understanding Home Healthcare',
        date: '2024-03-15',
        content: `
          Home healthcare is becoming increasingly important in modern healthcare delivery.
          This comprehensive guide will help you understand the benefits and processes involved
          in home healthcare services.

          ## Benefits of Home Healthcare

          1. Comfort of familiar surroundings
          2. Personalized care
          3. Cost-effective
          4. Better recovery outcomes

          ## How It Works

          Home healthcare providers work closely with patients and their families to create
          personalized care plans that address specific needs and goals.
        `
      },
      // Add more blog posts here
    };

    setPost(mockPosts[slug]);
  }, [slug]);

  if (!post) return <div>Loading...</div>;

  return (
    <PostContainer>
      <BackButton to="/#blog">‚Üê Back to Articles</BackButton>
      <PostTitle>{post.title}</PostTitle>
      <PostDate>{new Date(post.date).toLocaleDateString()}</PostDate>
      <PostContent>
        {post.content.split('\n').map((paragraph, index) => (
          <p key={index}>{paragraph.trim()}</p>
        ))}
      </PostContent>
    </PostContainer>
  );
};

export default BlogPost;
