import React from 'react';
import styled from 'styled-components';
import { motion, AnimatePresence } from 'framer-motion';
import { Link } from 'react-scroll';

const MenuOverlay = styled(motion.div)`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100vh;
  background: rgba(0, 0, 0, 0.5);
  z-index: 1000;
  display: none;

  @media (max-width: 768px) {
    display: block;
  }
`;

const MenuContainer = styled(motion.div)`
  position: fixed;
  top: 0;
  right: 0;
  width: 250px;
  height: 100vh;
  background: linear-gradient(135deg, var(--color-primary), var(--color-secondary));
  padding: 4rem 1.5rem 2rem;
  z-index: 1001;
  display: flex;
  flex-direction: column;
  gap: 1.5rem;
  box-shadow: -5px 0 20px rgba(0, 0, 0, 0.2);
  border-left: 1px solid rgba(255, 255, 255, 0.1);
  backdrop-filter: blur(10px);

  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    height: 2px;
    background: linear-gradient(
      90deg,
      transparent,
      rgba(255, 255, 255, 0.3),
      transparent
    );
  }
`;

const CloseButton = styled.button`
  position: absolute;
  top: 1rem;
  right: 1rem;
  background: rgba(255, 255, 255, 0.1);
  border: none;
  font-size: 1.5rem;
  cursor: pointer;
  color: white;
  width: 40px;
  height: 40px;
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
  transition: all 0.3s ease;

  &:hover {
    background: rgba(255, 255, 255, 0.2);
    transform: rotate(90deg);
  }
`;

const MobileNavLink = styled(Link)`
  font-size: 1.2rem;
  padding: 1rem;
  cursor: pointer;
  transition: all 0.3s ease;
  color: white;
  border-radius: 8px;
  position: relative;
  overflow: hidden;

  &::before {
    content: '';
    position: absolute;
    left: 0;
    bottom: 0;
    width: 0;
    height: 2px;
    background: var(--color-accent);
    transition: width 0.3s ease;
  }

  &:hover {
    color: white;
    background: rgba(255, 255, 255, 0.1);

    &::before {
      width: 100%;
    }
  }
`;

const MobileMenu = ({ isOpen, onClose }) => {
  return (
    <AnimatePresence>
      {isOpen && (
        <>
          <MenuOverlay
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            exit={{ opacity: 0 }}
            onClick={onClose}
          />
          <MenuContainer
            initial={{ x: 250 }}
            animate={{ x: 0 }}
            exit={{ x: 250 }}
            transition={{ type: "spring", damping: 20 }}
          >
            <CloseButton onClick={onClose}>Ã—</CloseButton>
            <MobileNavLink to="about" smooth={true} duration={500} onClick={onClose}>
              About
            </MobileNavLink>
            <MobileNavLink to="features" smooth={true} duration={500} onClick={onClose}>
              Features
            </MobileNavLink>
            <MobileNavLink to="testimonials" smooth={true} duration={500} onClick={onClose}>
              Testimonials
            </MobileNavLink>
            <MobileNavLink to="contact" smooth={true} duration={500} onClick={onClose}>
              Contact
            </MobileNavLink>
          </MenuContainer>
        </>
      )}
    </AnimatePresence>
  );
};

export default MobileMenu;
