import React from 'react';
import styled from 'styled-components';
import { Link } from 'react-router-dom';

const BlogContainer = styled.div`
  max-width: 1200px;
  margin: 0 auto;
  padding: 2rem;
`;

const BlogCard = styled.div`
  border: 1px solid #eaeaea;
  border-radius: 8px;
  padding: 1.5rem;
  margin-bottom: 2rem;
  transition: transform 0.2s;

  &:hover {
    transform: translateY(-5px);
    box-shadow: 0 4px 8px rgba(0,0,0,0.1);
  }
`;

const BlogTitle = styled.h2`
  color: #333;
  margin-bottom: 1rem;
`;

const BlogExcerpt = styled.p`
  color: #666;
  line-height: 1.6;
`;

const BlogDate = styled.span`
  color: #999;
  font-size: 0.9rem;
`;

const BlogList = () => {
  const blogPosts = [
    {
      id: 1,
      title: "Understanding Home Healthcare",
      excerpt: "Learn about the benefits of home healthcare and how it can improve quality of life...",
      date: "2024-03-15",
      slug: "understanding-home-healthcare"
    },
    // Add more blog posts here
  ];

  return (
    <BlogContainer>
      {blogPosts.map(post => (
        <BlogCard key={post.id}>
          <BlogDate>{new Date(post.date).toLocaleDateString()}</BlogDate>
          <BlogTitle>{post.title}</BlogTitle>
          <BlogExcerpt>{post.excerpt}</BlogExcerpt>
          <Link to={`/blog/${post.slug}`}>Read more â†’</Link>
        </BlogCard>
      ))}
    </BlogContainer>
  );
};

export default BlogList;
