import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { Link } from 'react-scroll';

const Nav = styled.nav`
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  height: 80px;
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 0 5%;
  background: ${props => props.scrolled ? 'white' : 'transparent'};
  transition: background 0.3s ease;
  z-index: 1000;
`;

const Logo = styled.div`
  font-family: var(--font-heading);
  font-size: 1.5rem;
  font-weight: 700;
  color: var(--color-primary);
`;

const NavLinks = styled.div`
  display: flex;
  gap: 2rem;
  align-items: center;

  @media (max-width: 768px) {
    display: none;
  }
`;

const NavLink = styled(Link)`
  cursor: pointer;
  font-weight: 500;
  transition: color 0.3s ease;

  &:hover {
    color: var(--color-primary);
  }
`;

const CTAButton = styled(Link)`
  background: var(--color-primary);
  color: white;
  padding: 0.8rem 1.5rem;
  border-radius: 4px;
  transition: background 0.3s ease;

  &:hover {
    background: var(--color-secondary);
  }
`;

const Navbar = () => {
  const [scrolled, setScrolled] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      setScrolled(window.scrollY > 50);
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  return (
    <Nav scrolled={scrolled}>
      <Logo>HomeNurse</Logo>
      <NavLinks>
        <NavLink to="about" smooth={true} duration={500}>About</NavLink>
        <NavLink to="features" smooth={true} duration={500}>Features</NavLink>
        <NavLink to="testimonials" smooth={true} duration={500}>Testimonials</NavLink>
        <CTAButton to="contact" smooth={true} duration={500}>Contact Us</CTAButton>
      </NavLinks>
    </Nav>
  );
};

export default Navbar;
